mapper.update:
UPDATE MatchWait
SET matchWait_match = true
WHERE receiver=#{receiver} and sender=#{sender}

UPDATE MatchWait
SET matchWait_status = true
WHERE receiver=#{receiver} or receiver=#{sender} or sender=#{receiver}

UPDATE user
SET status = true
WHERE loginId=#{sender} or loginId=#{receiver}

UPDATE MatchWait
SET matchWait_status = true
WHERE receiver=#{receiver} and sender=#{sender}
